function Modal({ children }) {
  const [isOpen, setIsOpen] = useState(false);

  return children({
    isOpen,
    open: () => setIsOpen(true),
    close: () => setIsOpen(false),
    toggle: () => setIsOpen(o => !o)
  });
}

